    }
}

public class LongestIncreasingSubArray{
    static int lengthOfLIS(int[] arr) { 
        int n = arr.length;
        if (n == 0) 
            return 0;  
        int[] lis = new int[n]; 
        lis[0] = 1;    
        int max = 1; 
        for (int i = 1; i < n; i++ ) 
        { 
            lis[i] = 1; 
            for (int j = 0; j < i; j++ )   
                if ( arr[i] > arr[j] && lis[i] < lis[j] + 1) 
                    lis[i] = lis[j] + 1; 
            if ( max < lis[i] ) 
                max = lis[i]; 
        } 
        return max;
    } 
