public static List<Integer> getPrimeFactors(int n) { 
    List<Integer> factors = new ArrayList<Integer>(); 
    for (int i = 2; i * i <= n; i++) { 
        while (n % i == 0) { 
            factors.add(i); 
            n /= i; 
        } 
    } 
    if (n > 1) {
        factors.add(n); 
    }
    return factors; 
}

